import React, { Component } from 'react';
import '../App.css';
import Web3 from 'web3'

import { COUNTRY_ABI, COUNTRY_ADDRESS } from '../config.js'

import Country from '../components/countries.js'

class Countries extends Component {

  async componentWillMount() {
    await this.runWeb3()
    await this.blockchain()
    await this.getData()
    this.forceUpdate()
  }


  componentDidMount(){
    document.title = "react-web3"
  }


  async componentWillUnmount() {
    clearInterval(this.interval);
  }


  constructor(props) {
    super(props)
    this.state = {
      account: '',
      balance: '',
      countries: [],
      loading: true
    }
  }


  async isInstalled() {
     if (typeof Web3 !== 'undefined'){
        console.log('Web3 Provider is installed')
     }
     else{
        console.log('No Web3 Provider!')
     }
  }

  async isLocked(web3) {
     web3.eth.getAccounts(function(err, accounts){
        if (err != null) {
           console.log(err)
        }
        else if (accounts.length === 0) {
           console.log('Web3 Provider is locked')
        }
        else {
           console.log('Web3 Provider is unlocked')
        }
     });
  }


  async runWeb3() {

    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. Please install MetaMask or similar!')
    }
  }


  async blockchain() {

    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545")

    this.isInstalled();
    this.isLocked(web3);

    const countryData = new web3.eth.Contract(COUNTRY_ABI, COUNTRY_ADDRESS)

    this.setState({ countryData })

    const accounts = await web3.eth.getAccounts()
    const balanceInWei = await web3.eth.getBalance(accounts[0])
    var balance = balanceInWei/1000000000000000000
    var account = accounts[0]

    console.log("Account: " + account)
    console.log("Balance: " + balance)

    this.setState({ account: accounts[0], balance: balance, loading: false })
  }


  async getData() {

    var countryCount = await this.state.countryData.methods.countryCount().call()

    const singleCountry = await this.state.countryData.methods.countries(0).call()

    console.log("Country: " + singleCountry)

    this.setState({
      countries: [singleCountry]
    })

/*
    for (var i = countryCount-1; i >= 0; i--) {
      const singleCountry = await this.state.countries.methods.countries(i).call()
      this.setState({
        countries: [...this.state.countries, singleCountry]
      })
    }
*/

  }

  render() {
    return (
      <div className="App">
        <header className="App-header">

        <ul>
          { this.state.countries.map((country, key) => {
            return(
              <div key={key}>
                <div>
                  <span>@{country.id}</span>
                  <p>{country.country}</p>
                  <p>{country.country}</p>
                  <p>{country.country}</p>
                  <p>{country.country}</p>
                  <p>{country.country}</p>
                </div>
              </div>
            )
          })}
        </ul>



        </header>
      </div>

    );
  }
}

export default Countries;
