import React, { Component } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import Web3 from 'web3'

import Home from './pages/home.js';


function isInstalled() {
   if (typeof Web3 !== 'undefined'){
      console.log('Web3 Provider is installed')
   }
   else{
      console.log('No Web3 Provider!')
   }
}

function isLocked(web3) {
   web3.eth.getAccounts(function(err, accounts){
      if (err != null) {
         console.log(err)
      }
      else if (accounts.length === 0) {
         console.log('Web3 Provider is locked')
      }
      else {
         console.log('Web3 Provider is unlocked')
      }
   });
}



class App extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
    this.forceUpdate()
  }


  componentDidMount(){
    document.title = "react-web3"
  }


  async componentWillUnmount() {
    clearInterval(this.interval);
  }


  constructor(props) {
    super(props)
    this.state = {
      account: '',
      balance: '',
      myString: '',
      loading: true
    }

    this.createThought = this.createThought.bind(this)
    this.changeHandle = this.changeHandle.bind(this)
    this.getOwnHandle = this.getOwnHandle.bind(this)
  }


  async loadWeb3() {

    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. Please install MetaMask or similar!')
    }
  }



  async loadBlockchainData() {

    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545")

    isInstalled();
    isLocked(web3);

    const accounts = await web3.eth.getAccounts()
    const balanceInWei = await web3.eth.getBalance(accounts[0])
    var balance = balanceInWei/1000000000000000000
    var account = accounts[0]

    this.setState({ profile })
    this.setState({ thoughtCount })


    this.setState({ account: accounts[0], balance: balance })
    this.setState({ loading: false })

  }


  render() {
    return (
       <BrowserRouter>
        <div>
            <Switch>
             <Route path="/" component={Home} exact/>

             {/*
            //<Route component={Error}/>
            */}

           </Switch>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
