import React, { Component } from 'react';
import Web3 from 'web3'

import { COUNTRY_ABI, COUNTRY_ADDRESS } from '../config.js'


class NewCountry extends Component {

  async componentWillMount() {
    await this.runWeb3()
    await this.blockchain()
    this.forceUpdate()
  }


  componentDidMount(){
    document.title = "react-web3"
  }


  async componentWillUnmount() {
    clearInterval(this.interval);
  }


  constructor(props) {
    super(props)
    this.state = {
      account: '',
      balance: '',
      loading: true
    }

    this.addData = this.addData.bind(this)
  }


  async isInstalled() {
     if (typeof Web3 !== 'undefined'){
        console.log('Web3 Provider is installed')
     }
     else{
        console.log('No Web3 Provider!')
     }
  }

  async isLocked(web3) {
     web3.eth.getAccounts(function(err, accounts){
        if (err != null) {
           console.log(err)
        }
        else if (accounts.length === 0) {
           console.log('Web3 Provider is locked')
        }
        else {
           console.log('Web3 Provider is unlocked')
        }
     });
  }


  async runWeb3() {

    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. Please install MetaMask or similar!')
    }
  }


  async blockchain() {

    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545")

    this.isInstalled();
    this.isLocked(web3);

    const countryData = new web3.eth.Contract(COUNTRY_ABI, COUNTRY_ADDRESS)

    this.setState({ countryData })

    const accounts = await web3.eth.getAccounts()
    const balanceInWei = await web3.eth.getBalance(accounts[0])
    var balance = balanceInWei/1000000000000000000
    var account = accounts[0]

    console.log("Account: " + account)
    console.log("Balance: " + balance)

    this.setState({ account: accounts[0], balance: balance, loading: false })
  }


   addData(name, total, perCap) {
     this.state.countryData.methods.addCountry(name, total, perCap, 63, 62, 61).send({ from: this.state.account })
   }


  render() {
    return (

      <div className = "formDiv">

        <form className = "thoughtForm" onSubmit={(event) => {
          event.preventDefault()
          this.addData(this.newName.value, this.newTotal.value, this.newPerCap.value)
        }}>
        <div class = "infoFlex">
        <span> &nbsp; </span>
          <span className="acctBalance">Account: {this.state.account}</span>
          <span className="handBalance">Balance: @{this.state.balance}</span>
          <span> &nbsp; </span>
        </div>
          <input ref={(input) => this.newName = input} type="text" className="addCountryForm" placeholder="Name" required />
          <input ref={(input) => this.newTotal = input} type="number" className="addCountryForm" placeholder="Total" required />
          <input ref={(input) => this.newPerCap = input} type="number" className="addCountryForm" placeholder="PerCap" required />


          <br />
            <input type="submit" hidden={false} />
        </form>

        <hr/>

      </div>


    );
  }
}

export default NewCountry;
